document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("nav-toggle"),t=document.getElementById("nav-menu");e&&t&&(e.addEventListener("click",function(){t.classList.toggle("active")}),t.querySelectorAll("a").forEach(n=>{n.addEventListener("click",()=>{t.classList.remove("active")})}),document.addEventListener("click",function(n){!e.contains(n.target)&&!t.contains(n.target)&&t.classList.remove("active")}));const c=window.location.pathname;t.querySelectorAll(".nav-link").forEach(a=>{a.getAttribute("href")===c&&a.classList.add("active")})});
